mapper.insert:

insert into a_topic (id, auid, uid, uid2, 
  topic_id, type, rights, 
  create_time, hot, status
  )
values (#{id,jdbcType=BIGINT}, #{auid,jdbcType=BIGINT}, #{uid,jdbcType=BIGINT}, #{uid2,jdbcType=BIGINT}, 
  #{topicId,jdbcType=BIGINT}, #{type,jdbcType=INTEGER}, #{rights,jdbcType=INTEGER}, 
  #{createTime,jdbcType=TIMESTAMP}, #{hot,jdbcType=BIGINT}, #{status,jdbcType=INTEGER}
  )

mapper.select:
SELECT COUNT(1) FROM `a_topic` a, topic b, user_profile c WHERE a.`type` = 1
AND gender = #{gender}
AND a.`topic_id` = b.`id` AND a.`uid` = c.`uid` AND ( b.`title` LIKE "%${titleName}%" OR c.`nick_name` LIKE "%${nickName}%" )
