mapper.select:
select count(1) total_records,
count(if(f.id>#{sinceId,jdbcType=BIGINT},TRUE,NULL)) as update_records,
max(f.id) as lastFragmentId,
count(if(f.id<m.firstNormalFragmentId,TRUE,NULL)) as firstDelCount
from topic_fragment f,(
    select f2.topic_id,min(if(f2.status=1,f2.id,NULL)) as firstNormalFragmentId
    from topic_fragment f2
    where f2.topic_id=#{topicId,jdbcType=BIGINT}
) m
where f.topic_id=m.topic_id and f.topic_id=#{topicId,jdbcType=BIGINT}


  select * from (

select

,

from topic_fragment f use index (idx_topic_id)
where f.topic_id=#{topicId,jdbcType=BIGINT}
order by id asc limit #{startIndex,jdbcType=INTEGER},#{offset,jdbcType=INTEGER}

  )a where a.id>#{sinceId,jdbcType=BIGINT}

select
count(1)
from topic_fragment where topic_id=#{topicId}
