mapper.select:
select t.*, (5000+c.read_count_dummy)*POW(0.95,datediff(now(),t.update_time)) as orderCount from topic t,content c where t.id=c.forward_cid and t.price>=#{minPrice} and c.status=0 and c.type=3 and t.status=0 and t.only_friend=0 and t.rights!=2 AND t.uid != #{uid}
and t.id NOT IN (${set})
AND NOT FIND_IN_SET(#{uid},SUBSTR(t.core_circle ,2, LENGTH(t.core_circle)-2)) order by orderCount DESC limit 1;

select t.*  from topic t
where t.price>=#{minPrice}
and t.status=0
and t.rights!=2
and t.only_friend=0
AND t.uid != #{uid}

  and t.id NOT IN (${set})

AND NOT FIND_IN_SET(#{uid},SUBSTR(t.core_circle ,2, LENGTH(t.core_circle)-2))
order by t.update_time DESC
limit 1;


